function hidesHeaderElements(){document.querySelector("header").classList.add("header--is-hidden")}function showsHeaderElements(){document.querySelector("header").classList.remove("header--is-hidden")}function openOrCloseAnswer(e){e.target.parentNode.classList.toggle("faq__questions__item--is-open")}function removeActiveButton(){var t=document.querySelectorAll("[data-tab-button]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__tabs__button--is-active")}function hideAllTabs(){var t=document.querySelectorAll("[data-tab-id]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__list--is-active")}document.addEventListener("DOMContentLoaded",function(){var t=document.querySelectorAll("[data-tab-button]"),s=document.querySelectorAll("[data-faq-question]");const n=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(e){(window.scrollY<n?hidesHeaderElements:showsHeaderElements)()});for(let e=0;e<t.length;e++)t[e].addEventListener("click",function(e){var t=e.target.dataset.tabButton,t=document.querySelector(`[data-tab-id=${t}]`);hideAllTabs(),t.classList.add("shows__list--is-active"),removeActiveButton(),e.target.classList.add("shows__tabs__button--is-active")});for(let e=0;e<s.length;e++)s[e].addEventListener("click",openOrCloseAnswer)});